import React,{useState} from 'react';

import './PlaceItems.css';
import Card from '../../shared/components/UIElement/Card/Card';
import Button from '../../shared/components/formUI/Button/Button';
import '../../shared/components/formUI/Button/Button.css';

const PlaceItem = props => {
  const [showMap, setShowMap] = useState(false);
  const openModalMap = () => setShowMap(true);

  const closeModalMap = () => setShowMap(false);

  return (
    <li className="place-item">
      <Card className="place-item__content">
        <div className="place-item__image">
          <img src={props.image} alt={props.title} />
        </div>

        <div className="place-item__info">
          <h2>{props.title}</h2>
          <h3>{props.address}</h3>
          <p>{props.description}</p>
        </div>
        <div className="place-item__actions">
          <Button inverse>View On Map</Button>
          <Button to={`/places/${props.id}`}>Edit</Button>
          <Button danger>Delete</Button>
        </div>
      </Card>
    </li>
  );
}

export default PlaceItem;
